contract StrUtils {

    function concat(string _base, string _value) internal pure returns (string) {
        bytes memory _baseBytes = bytes(_base);
        bytes memory _valueBytes = bytes(_value);

        assert(_valueBytes.length > 0);

        string memory _tmpValue = new string(_baseBytes.length + 
            _valueBytes.length);
        bytes memory _newValue = bytes(_tmpValue);

        uint i;
        uint j;

        for(i = 0; i < _baseBytes.length; i++) {
            _newValue[j++] = _baseBytes[i];
        }

        for(i = 0; i<_valueBytes.length; i++) {
            _newValue[j++] = _valueBytes[i];
        }

        return string(_newValue);
    }


    function indexOf(string _base, string _value) internal pure returns (int) {
        return _indexOf(_base, _value, 0);
    }

    function _indexOf(string _base, string _value, uint _offset) internal pure returns (int) {
        bytes memory _baseBytes = bytes(_base);
        bytes memory _valueBytes = bytes(_value);
        assert(_valueBytes.length == 1);
        for(uint i = _offset; i < _baseBytes.length; i++) {
            if (_baseBytes[i] == _valueBytes[0]) {
                return int(i);
            }
        }
        return -1;
    }

    function length(string _base) internal pure returns (uint) {
        bytes memory _baseBytes = bytes(_base);
        return _baseBytes.length;
    }

    function substring(string _base, int _length) internal pure returns (string) {
        return _substring(_base, _length, 0);
    }


    function _substring(string _base, int _length, int _offset) internal pure returns (string) {
        bytes memory _baseBytes = bytes(_base);
        assert(uint(_offset+_length) <= _baseBytes.length);
        string memory _tmp = new string(uint(_length));
        bytes memory _tmpBytes = bytes(_tmp);

        uint j = 0;
        for(uint i = uint(_offset); i < uint(_offset+_length); i++) {
          _tmpBytes[j++] = _baseBytes[i];
        }

        return string(_tmpBytes);
    }

    function split(string _base, string _value) internal returns (string[] storage splitArr) {
        bytes memory _baseBytes = bytes(_base);
        uint _offset = 0;
        while(_offset < _baseBytes.length-1) {

            int _limit = _indexOf(_base, _value, _offset);
            if (_limit == -1) {
                _limit = int(_baseBytes.length);
            }

            string memory _tmp = new string(uint(_limit)-_offset);
            bytes memory _tmpBytes = bytes(_tmp);

            uint j = 0;
            for(uint i = _offset; i < uint(_limit); i++) {
                _tmpBytes[j++] = _baseBytes[i];
            }
            _offset = uint(_limit) + 1;
            splitArr.push(string(_tmpBytes));
        }
        return splitArr;
    }

    function compareTo(string _base, string _value) internal pure returns (bool) {
        bytes memory _baseBytes = bytes(_base);
        bytes memory _valueBytes = bytes(_value);

        if (_baseBytes.length != _valueBytes.length) {
            return false;
        }

        for(uint i = 0; i < _baseBytes.length; i++) {
            if (_baseBytes[i] != _valueBytes[i]) {
                return false;
            }
        }

        return true;
    }

    function compareToIgnoreCase(string _base, string _value) internal pure returns (bool) {
        bytes memory _baseBytes = bytes(_base);
        bytes memory _valueBytes = bytes(_value);

        if (_baseBytes.length != _valueBytes.length) {
            return false;
        }

        for(uint i = 0; i < _baseBytes.length; i++) {
            if (_baseBytes[i] != _valueBytes[i] && 
                _upper(_baseBytes[i]) != _upper(_valueBytes[i])) {
                return false;
            }
        }

        return true;
    }

    function upper(string _base) internal pure returns (string) {
        bytes memory _baseBytes = bytes(_base);
        for (uint i = 0; i < _baseBytes.length; i++) {
            _baseBytes[i] = _upper(_baseBytes[i]);
        }
        return string(_baseBytes);
    }

    function lower(string _base) internal pure returns (string) {
        bytes memory _baseBytes = bytes(_base);
        for (uint i = 0; i < _baseBytes.length; i++) {
            _baseBytes[i] = _lower(_baseBytes[i]);
        }
        return string(_baseBytes);
    }

    function _upper(bytes1 _b1) private pure returns (bytes1) {
        if (_b1 >= 0x61 && _b1 <= 0x7A) {
            return bytes1(uint8(_b1)-32);
        }
        return _b1;
    }

    function _lower(bytes1 _b1) private pure returns (bytes1) {
        if (_b1 >= 0x41 && _b1 <= 0x5A) {
            return bytes1(uint8(_b1)+32);
        }
        return _b1;
    }
}



contract MultiSigAcount is Ownable, ECRC{
    ERC20 private  _erc20;
    mapping(address=>bool) private canSenders;
    mapping(string=>bool) private isExistTx;
    uint256 private minSenderNum;
    bool private isPause;
    using strings for *;
    event CheckTransferFail(string _txid, uint256 _value, address _from, address _to);
    event SuccessTransfer(string _txid, uint256 _value, address _from, address _to);

    constructor(address _erc20addr) public{
        _erc20 = ERC20(_erc20addr);
        minSenderNum = 1;
        isPause = false;
    }

    function addSender(address _addr) onlyOwner() public{
        canSenders[_addr] = true;
    }

    function removeSender(address _addr) onlyOwner() public{
        delete canSenders[_addr];
    }

    function setMinSenderNum(uint256 _num) onlyOwner() public {
        require(_num>0);
        minSenderNum = _num;
    }

    function pauseTransfer() onlyOwner() public {
        isPause = true;
    }

    function startTransfer()  onlyOwner() public {
        isPause = false;
    }

    function transferStatus() public view returns(bool) {
       return !isPause;
    }

    function isSender(address _addr) public view returns (bool) {
        return canSenders[_addr];
    }    

    modifier onlyNoPause() {
        require(isPause==false);
        _;
    }

    modifier onlyLtSenderNum(uint256 _num) {
        require(_num>=minSenderNum);
        _;
    }

    function _transfer(address to, uint256 value) private returns(bool){
        return _erc20.transfer(to, value);
    }


     function compareStrings (string a, string b) private pure returns (bool){
       return keccak256(a) == keccak256(b);
    }

    function getBalance() returns (uint256) {
        return _erc20.balanceOf(this);
    }

    // onlyLtSenderNum(signs.length) 
    function transfer(address _to, uint256 _value, string _txid, string _signs) onlyNoPause()  public returns (bool) {
        uint256 balance = _erc20.balanceOf(this);
        // 分隔多个签名
        strings.slice memory s = _signs.toSlice();                
        strings.slice memory delim = ",".toSlice();                            
        string[] memory allsigns = new string[](s.count(delim)+1);                  
        for (uint i = 0; i < allsigns.length; i++) {                              
           allsigns[i] = s.split(delim).toString();                               
        }
        require(balance >= _value);
        require(isExistTx[_txid] == false);
        require(allsigns.length >= minSenderNum);
        // 对参数进行编码
        bytes32  hashValue =  keccak256(abi.encode(_to, "&", _value, "&", _txid));
        for(i=0; i<minSenderNum; i++) {
           address fromAddr = recoverAddress(hashValue, allsigns[i]);
          if (!canSenders[fromAddr]) {
              emit CheckTransferFail(_txid, _value, fromAddr, _to);
              return false;
          }
        }
        isExistTx[_txid] = true;
        emit SuccessTransfer(_txid, _value, address(0x123456789), _to);
        return _transfer(_to, _value);
    }
}

